mapper.update:
	UPDATE notice_reply
    SET regroup = #{rnum}
    WHERE rnum = #{rnum}

mapper.select:
SELECT rnum, bnum, replytext, replyer, date, regroup, restep, reindent 
FROM notice_reply
WHERE rnum = #{rnum}

mapper.update:
UPDATE notice_reply
SET replytext = #{replytext}
WHERE rnum = #{rnum}

UPDATE notice_reply
SET restep = restep + 1
WHERE regroup = #{regroup} AND restep > #{restep}
